import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import '../App.css'
import { Link } from 'react-router-dom'
import Table from "react-bootstrap/Table";
import Card from 'react-bootstrap/Card';



function CategoryDetail() {
    const [category, setCategory] = useState(null);
    const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const { categoryId } = useParams();

  useEffect(() => {
    axios
      .get(`/api/categories/${categoryId}`)
      .then((response) => {
        // console.log(categoryId);
          setCategory(response.data);
          // console.log(response.data);
        setLoading(false);
      }) 
      .catch((error) => {
        console.error("Error fetching category:", error);
        setLoading(false);
      });
    //   products
      axios
        .get(`/api/products/get-products-by-category/${categoryId}`)
        .then((response) => {
          setProducts(response.data);
        })
        .catch((error) => {
          console.error("Error fetching products:", error);
        });

  }, [categoryId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!category) {
    return <div>Category not found</div>;
  }

  return (
    <div className="container">
      <div className="buttons_top w-100">
        <Link
          to={`/category/${categoryId}/create-Subcategory`}
          className="btn btn-primary justify-content-end"
        >
          Add SubCategory
        </Link>
      </div>
      <br/>
      <h1 className="">{category.name}</h1>
      <br/>
      <h4>Subcategories({category.subcategories.length})</h4>
      <ul className="subcat_list">
        {category.subcategories.map((subcategory) => (
          <Link to={`/category/${categoryId}/${subcategory._id}`}>
            <li key={subcategory._id}>{subcategory.name}</li>
          </Link>
        ))}
      </ul>

      <Card className="subcat_list" >
      <Card.Body >
        <Card.Title>Card Title</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Card.Link href="#">Card Link</Card.Link>
        <Card.Link href="#">Another Link</Card.Link>
      </Card.Body>
    </Card>

      <Table striped>
        <thead>
          <tr>
            <th>Products</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product._id}>
              <td>{product.name}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default CategoryDetail;
